import {action, observable,set} from "mobx";
import ApiClient from "../clients/ApiClient";

class AppStore {
  stations = observable([]);
  lines = observable(['N','Q','R','W','1','2','3','A','C','E'])
  currentView = observable.box('stations');

  apiClient = new ApiClient();

  switchView = action(() => {
    if (this.currentView.get() === 'stations') {
      set(this.currentView, 'lines')
    } else if (this.currentView.get() === 'lines') {
      set(this.currentView, 'stations')
    }
  })

  getStations = action(() => {
    this.apiClient.get('http://localhost:8080/api/v1/stations')
      .then((data) => {
        set(this.stations,data);
      })
  })

}

export default AppStore;
