import {action, observable,set,get} from "mobx";
import ApiClient from "../clients/ApiClient";

class AppStore {
  stations = observable([]);
  lines = observable(['N','Q','R','W','1','2','3','A','C','E'])
  currentView = observable.box('stations');

  apiClient = new ApiClient();

  switchView = action(() => {
    console.log(get(this.currentView))
    if (get(this.currentView) === 'stations') {
      this.currentView ='lines'
    } else if (this.currentView === 'lines') {
      this.currentView = 'stations'
    }
    console.log(this.currentView)
  })

  getStations = action(() => {
    this.apiClient.get('http://localhost:8080/api/v1/stations')
      .then((data) => {
        set(this.stations,data);
      })
  })

}

export default AppStore;
