import React, { Component } from 'react';
import {inject, observer} from "mobx-react";

class StationContainer extends Component {

  componentDidMount() {
    this.props.appStore.getStations()
  }

  renderView = () => {
    let data;
    if (this.props.appStore.currentView.get() === 'stations') {
      data = this.props.appStore.stations
    } else if (this.props.appStore.currentView.get() === 'lines') {
      data = this.props.appStore.lines
    }

    return data.map(
      station => <div data-station key={station.id}>{station.name}</div>
    )
  };

  switchView = () => {
    this.appStore.switchView()
  }

  render() {
    return (
      <div>
        <button onClick={this.switchView}>Swap view</button>
        {this.renderView()}
      </div>
    );
  }
}


export default inject(['appStore'])(observer(StationContainer));
