import React, { Component } from 'react'

class TransitMode extends Component {
  render() {
    return (
      <div>
        <div>
          <span>{this.props.mode.name} ({this.props.mode.counter})</span>
          <span>
    <Mutation
    mutation={ADD_MODE}
    variables={{ name: this.state.name }}
    update={(store, { data: { addMode } } ) => {
      console.log(addMode)
      const currentStore = store.readQuery({ query: GET_MODES })
      currentStore.modes.unshift(addMode)
      store.writeQuery({
        query: GET_MODES,
        currentStore
      })
    }}
  >

    {(addModeFunc) => (
    <button onClick={addModeFunc}>
      Submit
      </button>
    )}
  </Mutation>
      </span>
        </div>
      </div>
    )
  }
}

export default TransitMode
