import {action, extendObservable} from "mobx";

class ListStore {

  constructor(apiClient) {
    this.apiClient = apiClient;

    extendObservable(this, {
      lists: [],
      getLists: action(() => {
        this.apiClient.get('http://localhost:8080/api/v1/pinelist')
          .then((data) => {
            console.log(data)
            this.stations = data;
          })
      })
    })
  }
}
 export default ListStore
