import {action, observable, set, get, extendObservable} from "mobx";
import ApiClient from "../clients/ApiClient";

class AppStore {

  constructor(apiClient) {
    this.apiClient = apiClient;
    this.stations = observable([]);
    this.lines = observable([{
      name: 'N',
      id: 'N'
    }])
    this.currentView = observable.object('stations');
  }


  //
  // apiClient = new ApiClient();
  //
  switchView = action(() => {
    console.log(this.currentView)
    if (this.currentView.get() === 'stations') {
      this.currentView.set('lines')
    } else if (this.currentView.get() === 'lines') {
      this.currentView.set('stations')
    }
    console.log(this.currentView)
  })

  getStations = action(() => {
    this.apiClient.get('http://localhost:8080/api/v1/stations')
      .then((data) => {
        set(this.stations,data);
      })
  })

}

export default AppStore;
