import React, { Component } from 'react';
import {inject, observer} from "mobx-react";

class StationContainer extends Component {

  state = {
    rerender: 0
  }

  componentDidMount() {
    this.props.appStore.getStations()
  }

  renderStations = () => (
    this.props.appStore.stations.map(station => <div key={station.id}>{station.name}</div>)
  );

  rerender = () => {
    this.setState({ rerender: Math.random() })
  }

  render() {
    console.log('rendering')
    return (
      <div>
        <div>{this.state.rerender}</div>

        <button onClick={this.rerender}>New</button>

        {this.renderStations()}


      </div>
    );
  }
}


export default inject(['appStore'])(observer(StationContainer));
